import './Input.css';

const Input = (props) => {
  const { addItem, getInput } = props;

  const onChangeHandler = (e) => {
    getInput(e.target.value);
  };

  return (
    <>
      <input className="inputComponent" onChange={onChangeHandler}></input>
      <button className="addButton inputComponent" onClick={addItem}>
        Add Item
      </button>
    </>
  );
};

export default Input;
