import './Input.css';

const Input = (props) => {
  const { addItem } = props;
  const [input, setInput] = useState('');

  const onChangeHandler = () => {
    getInput(input);
  };

  return (
    <>
      <input className="inputComponent" onChange={onChangeHandler}></input>
      <button className="addButton inputComponent" onClick={addItem}>
        Add Item
      </button>
    </>
  );
};

export default Input;
